        //Exit button component 
    private void exitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_exitButtonActionPerformed
           
        //check if JOptionPane dialog box is a yes option which is 0    
        if(JOptionPane.showConfirmDialog(null,"Confirm if you want to exit", "Pharmacy Management Systems", JOptionPane.YES_NO_OPTION) 
                  == JOptionPane.YES_NO_OPTION)
            //if yes
              System.exit(0); // then exit the program
              
    }//GEN-LAST:event_exitButtonActionPerformed

