    private void PasswordFieldActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PasswordFieldActionPerformed
        
    }//GEN-LAST:event_PasswordFieldActionPerformed
    int attempt = 3; //variable to control user attempts, user has only three attempts initially
    
    //componet that will make the window components visible again
    private void loginButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonActionPerformed
    //local variables to get inputs in these components
    String username = usernameTextField.getText();
    String password = PasswordField.getText();
   
//check if contents of username and password equals to the given strings
    if ( username.contains("aljebra") && password.contains("aljebra2117"))
    {   //if yes
        
        usernameTextField.setText(""); //clear this component
        PasswordField.setText("");      //clear this component
        
        //make the components on the window (JFrame) visible again 
        doctorButton.setEnabled(true);
        pharmacyButton.setEnabled(true);
        patientButton.setEnabled(true);
        paymentButton.setEnabled(true);
        gpaButton.setEnabled(true);
        pharOfficeButton.setEnabled(true);
        surveyButton.setEnabled(true);
        hospitalButton.setEnabled(true);
        specialistButton.setEnabled(true);
        consultantButton.setEnabled(true);
        pharmacistButton.setEnabled(true);
        helpButton.setEnabled(true);

     
    }
    //if condition is false
    else if (!( username.contains("aljebra") && password.contains("aljebra2117")))
    {
        usernameTextField.setText("");  //clear this component
        PasswordField.setText("");     //clear this component
        
        //make the components on the window (JFrame) not visible         
        doctorButton.setEnabled(false);
        pharmacyButton.setEnabled(false);
        patientButton.setEnabled(false);
        paymentButton.setEnabled(false);
        gpaButton.setEnabled(false);
        pharOfficeButton.setEnabled(false);
        surveyButton.setEnabled(false);
        hospitalButton.setEnabled(false);
        specialistButton.setEnabled(false);
        consultantButton.setEnabled(false);
        pharmacistButton.setEnabled(false);
        helpButton.setEnabled(false);
        
        --attempt;  //predecrement the attempt variable for 3
        
        //show JPane window to tell the user the number of attempts remaining
        JOptionPane.showMessageDialog(null, "Remaining attempts: " + attempt,  "Pharmacy Management Systems", JOptionPane.INFORMATION_MESSAGE);
        
        
        if( attempt == 0) //3 attempts exhausted 
            System.exit(0); //exit the system

    }
        
    }//GEN-LAST:event_loginButtonActionPerformed

